<!--button product--><div class="col-md-12" align="center" id="f_new"><div class="btn-group" role="group" aria-label="...">  <button type="button" class="btn btn-info active">Produk Terkini</button>  <button type="button" class="btn btn-warning" id="new">Produk Terlaris</button></div></div><!--end button product--><!--food contain--><div class="col-md-12" style="border: 1px solid #DFDFD0; border-radius:2px" id="food"><!--1st Produk Terkini --><?php //$result_p = $db_handle->runQuery("SELECT * FROM product ORDER BY id ASC LIMIT 9");// senarai makanan$produkTerkini = $this->terkini;$gambar = URL . 'images/makanan ringan/';if (!empty($produkTerkini)) :	foreach($produkTerkini as $key=>$value):# tawaran dan harga	$tawaran = ($produkTerkini[$key]['offer']==0) ? null : 		"\n\t\t\t\t\t" . '<strike>RM: ' 		. ringgit($produkTerkini[$key]['offer']) . '</strike>';	$harga = ($produkTerkini[$key]['price']==0) ? 		"\n\t\t\t\t\t<strong>RM: " . ringgit(0) . '</strong>': 		"\n\t\t\t\t\t<strong>RM: "		. ringgit($produkTerkini[$key]['price']) . '</strong>';?>	<div class="col-sm-6 col-md-4">		<div class="caption">			<h4 class="h4"><?php echo $produkTerkini[$key]['com'] ?></h4>		</div>		<div class="thumbnail thumbnail1">		<a class="example-image-link" href="<?php echo $gambar . $produkTerkini[$key]['pic']?>" data-lightbox="example-set" 		title="Click on the right side of the image to move forward.">		<img src="<?php echo $gambar . $produkTerkini[$key]['pic']?>" alt="..." class="img-responsive" style="height:150px"></a>			<div class="caption">				<h4><?php echo $produkTerkini[$key]['title'] ?></h4>				<div class="alert alert-info"><?php 				echo $tawaran . $harga . "\n\t\t\t\t\t" ?></div>			</div>		</div>	</div><?php 	endforeach;endif;?></div><!--end food contain-->